import React, { Component, useEffect, useState } from "react";
import { faWrench } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import ReactPaginate from 'react-paginate';
import { useRef } from "react";
import '../index.css';

export default function AdminHome({ userData }) {

  //setting state
  const [data, setData] = useState([]);
  const [limit,setLimit]=useState(20);
  const [pageCount,setPageCount]=useState(0);
  const currentPage=useRef(0);



  useEffect(() => {
    currentPage.current=0;
    // getAllUser();
    getPaginatedUsers();
  }, []);


  //fetching all user
  const getAllUser = () => {
    fetch("http://localhost:8084/api/v1/teacher/users", {
      method: "GET",
      crossDomain: true,
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json",
        "Access-Control-Allow-Origin": "*",
        Authorization: `Bearer ${window.localStorage.getItem("token")}`,
      }
    })
      .then((res) => res.json())
      .then((data) => {
        console.log(data, "userData");
        setData(data.content);
      });
  };



//logout
  const logOut = () => {
    window.localStorage.clear();
    window.location.href = "./sign-in";
  };

 
  //mode5 user
  function changeRole(nickname)  {
    if (window.confirm(`Estas seguro de que quieres cambiar el rol del usuario ${nickname}`)) {
      fetch("http://localhost:8084/api/v1/teacher/changeRole", {
        method: "POST",
        crossDomain: true,
        headers: {
          "Content-Type": "application/json",
          Accept: "application/json",
          "Access-Control-Allow-Origin": "*",
          Authorization: `Bearer ${window.localStorage.getItem("token")}`,
        },
        body: JSON.stringify({
          username: nickname,
        }),
      })
      .then((res) => res.json())
        .then((data) => {
          alert("Rol Actualizado Correctamente");
          getPaginatedUsers();
        });
      
      
      
    } else {

    }
  };

  //pagination
  function handlePageClick(e) {
    console.log(e);
    currentPage.current=e.selected+1;
    getPaginatedUsers();
   

  }
  function changeLimit(){
    currentPage.current=0;
    getPaginatedUsers();
  }

  function getPaginatedUsers(){
    fetch(`http://localhost:8084/api/v1/teacher/users?page=${currentPage.current}&limit=${limit}`, {
      method: "GET",
      crossDomain: true,
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json",
        "Access-Control-Allow-Origin": "*",
        Authorization: `Bearer ${window.localStorage.getItem("token")}`,
      }
    })
      .then((res) => res.json())
      .then((data) => {
        console.log(data, "userData");
        setPageCount(data.totalPages);
        setData(data.content)
      });
  }

  return (
    <div className="auth-wrapper">
      <div className="auth-inner">
        <h3>Welcome Teacher</h3>
        <table className="table">
          <thead>
            <tr>
              <th>Nombre de Usuario</th>
              <th>Correo Electrónico</th>
              <th>Tipo de usuario</th>
              <th>Cambiar a Moderador</th>
            </tr>
          </thead>
          <tbody>
            {data.map((item, index) => (
              <tr key={index}>
                <td>{item.nickname}</td>
                <td>{item.email}</td>
                <td>{item.role}</td>
                <td>
                  <button className="btn btn-sm btn-secondary" onClick={() => changeRole(item.nickname)}>
                    <FontAwesomeIcon icon={faWrench} />
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
        <div className="pagination-container">
          <ReactPaginate
            breakLabel="..."
            nextLabel="Next >"
            onPageChange={handlePageClick}
            pageRangeDisplayed={5}
            pageCount={pageCount}
            previousLabel="< Previous"
            marginPagesDisplayed={2}
            containerClassName="pagination justify-content-center"
            pageClassName="page-item"
            pageLinkClassName="page-link"
            previousClassName="page-item"
            previousLinkClassName="page-link"
            nextClassName="page-item"
            nextLinkClassName="page-link"
            activeClassName="active"
            forcePage={currentPage - 1}
          />
          <input className="form-control limit-input" placeholder="Limit" onChange={e => setLimit(e.target.value)} />
          <button className="btn btn-primary set-limit-button" onClick={changeLimit}>Set Limit</button>
          <button className="btn btn-primary log-out-button" onClick={logOut}>Log Out</button>
        </div>
      </div>
    </div>
  );
}
