import React, { useState, useEffect } from 'react';
import {
  TextField,
  IconButton,
  List,
  ListItem,
  ListItemText,
  ListItemAvatar,
  Avatar,
  Typography,
} from '@material-ui/core';
import Stomp from 'stompjs';
import SockJS from 'sockjs-client';

const App = () => {
  const [messages, setMessages] = useState([]);
  const [message, setMessage] = useState('');
  const [sender, setSender] = useState('');
  const [stompClient, setStompClient] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Conectar al WebSocket
    const socket = new SockJS('http://localhost:8080/ws');
    const client = Stomp.over(socket);

    // Configurar encabezados de conexión
    const headers = {
      'Authorization': 'eyJhbGciOiJIUzI1NiJ9.eyJzdWIiOiJzdHVkZW50QGdtYWlsLmNvbSIsImlhdCI6MTcwODI0NDc3MiwiZXhwIjoxNzA4MjQ2MjEyfQ.oFEGY3fpf_du0oOxN9bgN2Oj7b5fHU2fUm0ksYXKGOY' // Aquí colocas tu token
    };

    client.connect(headers, () => {
      client.subscribe('/topic/messages', (message) => {
        const receivedMessage = JSON.parse(message.body);
        setMessages((prevMessages) => [...prevMessages, receivedMessage]);
      });
    });

    setStompClient(client);

    // Precargar los mensajes desde el servidor
    fetch('http://localhost:8080/messages', {
      headers: {
        'Authorization': 'eyJhbGciOiJIUzI1NiJ9.eyJzdWIiOiJzdHVkZW50QGdtYWlsLmNvbSIsImlhdCI6MTcwODI0NDc3MiwiZXhwIjoxNzA4MjQ2MjEyfQ.oFEGY3fpf_du0oOxN9bgN2Oj7b5fHU2fUm0ksYXKGOY' // Aquí colocas tu token
      }
    })
      .then(response => response.json())
      .then(data => {
        setMessages(data);
        setLoading(false);
      })
      .catch(error => {
        console.error('Error fetching messages:', error);
        setLoading(false);
      });

    return () => {
      client.disconnect();
    };
  }, []);

  const handleSenderChange = (event) => {
    setSender(event.target.value);
  };

  const handleMessageChange = (event) => {
    setMessage(event.target.value);
  };

  const sendMessage = () => {
    if (message.trim()) {
      const chatMessage = {
        sender,
        content: message,
      };

      stompClient.send('/app/chat', {}, JSON.stringify(chatMessage));
      setMessage('');
    }
  };

  return (
    <div>
      {loading ? (
        <Typography variant="subtitle1">Loading...</Typography>
      ) : (
        <List>
          {messages.length === 0 ? (
            <Typography variant="subtitle1">No messages yet</Typography>
          ) : (
            messages.map((msg, index) => (
              <ListItem key={index}>
                <ListItemAvatar>
                  <Avatar>{msg.sender.charAt(0)}</Avatar>
                </ListItemAvatar>
                <ListItemText
                  primary={
                    <Typography variant="subtitle1">{msg.sender}</Typography>
                  }
                  secondary={msg.content}
                />
              </ListItem>
            ))
          )}
          <div style={{ display: 'flex', alignItems: 'center' }}>
            <TextField
              placeholder="Enter your sender"
              value={sender}
              onChange={handleSenderChange}
              autoFocus
            />
            <TextField
              placeholder="Type a message"
              value={message}
              onChange={handleMessageChange}
              fullWidth
            />
            <IconButton onClick={sendMessage} disabled={!message.trim()}>
              send
            </IconButton>
          </div>
        </List>
      )}
    </div>
  );
};

export default App;
