import React, { Component, useState, useEffect } from "react";
import SockJS from 'sockjs-client'; // Asegúrate de haber instalado 'sockjs-client' usando npm o yarn
import Stomp from 'stompjs';
import { Typography, List, ListItem, ListItemAvatar, Avatar, ListItemText, TextField, IconButton } from '@material-ui/core';

// Definición del componente
export default function Chat() {
  
    const [messages, setMessages] = useState([]);
    const [message, setMessage] = useState('');
    const [sender, setSender] = useState('');
    const [stompClient, setStompClient] = useState(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        // Conectar al WebSocket
        const socket = new SockJS('http://localhost:8084/api/v1/websocket');
        const client = Stomp.over(socket);

        client.connect({}, () => {
        client.subscribe('/topic/messages', (message) => {
            const receivedMessage = JSON.parse(message.body);
            setMessages((prevMessages) => [...prevMessages, receivedMessage]);
        });
        });

        setStompClient(client);

        // Precargar los mensajes desde el servidor
        fetch('http://localhost:8084/messages',{
            method: "GET",
            crossDomain: true,
            headers: {
                "Content-Type": "application/json",
                Accept: "application/json",
                "Access-Control-Allow-Origin": "*",
                Authorization: `Bearer ${window.localStorage.getItem("token")}`,
            }
        })
        .then(response => response.json())
        .then(data => {
            setMessages(data);
            setLoading(false);
        })
        .catch(error => {
            console.error('Error fetching messages:', error);
            setLoading(false);
        });

        return () => {
        client.disconnect();
        };
    }, []);

    const handleSenderChange = (event) => {
        setSender(event.target.value);
    };

    const handleMessageChange = (event) => {
        setMessage(event.target.value);
    };

    const sendMessage = () => {
        if (message.trim()) {
        const chatMessage = {
            sender,
            content: message,
        };

        stompClient.send('/app/room', {}, JSON.stringify(chatMessage));
        setMessage('');
        }
    };

    return (
        <div>
        {loading ? (
            <Typography variant="subtitle1">Loading...</Typography>
        ) : (
            <List>
            {messages.length === 0 ? (
                <Typography variant="subtitle1">No messages yet</Typography>
            ) : (
                messages.map((msg, index) => (
                <ListItem key={index}>
                    <ListItemAvatar>
                    <Avatar>{msg.sender.charAt(0)}</Avatar>
                    </ListItemAvatar>
                    <ListItemText
                    primary={
                        <Typography variant="subtitle1">{msg.sender}</Typography>
                    }
                    secondary={msg.content}
                    />
                </ListItem>
                ))
            )}
            <div style={{ display: 'flex', alignItems: 'center' }}>
                <TextField
                placeholder="Enter your sender"
                value={sender}
                onChange={handleSenderChange}
                autoFocus
                />
                <TextField
                placeholder="Type a message"
                value={message}
                onChange={handleMessageChange}
                fullWidth
                />
                <IconButton onClick={sendMessage} disabled={!message.trim()}>
                send
                </IconButton>
            </div>
            </List>
        )}
        </div>
    );
}
