import React, { Component } from 'react'; // Importa React y Component desde 'react'
import * as Components from './Components';

// Definición del componente
class SignUp extends Component { // Extiende la clase desde Component

  constructor(props) {
    super(props);
    this.state = { signIn: true };
    // No es necesario hacer bind en una arrow function
  }

  toggleSignIn = () => {
    this.setState(prevState => ({ signIn: !prevState.signIn }));
  }

  render() {
    return (
      <Components.Container>
        <Components.SignUpContainer signinIn={this.state.signIn}> {/* Utiliza this.state.signIn */}
          <Components.Form>
            <Components.Title>Create Account</Components.Title>
            <Components.Input type='text' placeholder='Name' />
            <Components.Input type='email' placeholder='Email' />
            <Components.Input type='password' placeholder='Password' />
            <Components.Button>Sign Up</Components.Button>
          </Components.Form>
        </Components.SignUpContainer>

        <Components.SignInContainer signinIn={this.state.signIn}> {/* Utiliza this.state.signIn */}
          <Components.Form>
            <Components.Title>Sign in</Components.Title>
            <Components.Input type='email' placeholder='Email' />
            <Components.Input type='password' placeholder='Password' />
            <Components.Anchor href='#'>Forgot your password?</Components.Anchor>
            <Components.Button>Sigin In</Components.Button>
          </Components.Form>
        </Components.SignInContainer>

        <Components.OverlayContainer signinIn={this.state.signIn}> {/* Utiliza this.state.signIn */}
          <Components.Overlay signinIn={this.state.signIn}> {/* Utiliza this.state.signIn */}

            <Components.LeftOverlayPanel signinIn={this.state.signIn}> {/* Utiliza this.state.signIn */}
              <Components.Title>Welcome Back!</Components.Title>
              <Components.Paragraph>
                To keep connected with us please login with your personal info
              </Components.Paragraph>
              <Components.GhostButton onClick={this.toggleSignIn}> {/* Utiliza this.toggleSignIn */}
                Sign In
              </Components.GhostButton>
            </Components.LeftOverlayPanel>

            <Components.RightOverlayPanel signinIn={this.state.signIn}> {/* Utiliza this.state.signIn */}
              <Components.Title>Hello, Friend!</Components.Title>
              <Components.Paragraph>
                Enter Your personal details and start journey with us
              </Components.Paragraph>
              <Components.GhostButton onClick={this.toggleSignIn}> {/* Utiliza this.toggleSignIn */}
                Sigin Up
              </Components.GhostButton>
            </Components.RightOverlayPanel>

          </Components.Overlay>
        </Components.OverlayContainer>

      </Components.Container>
    );
  }
}

export default SignUp;
